import React, { ChangeEvent, FC, useState } from 'react';

export const Invite: FC = () => {
  const [text, setText] = useState('');
  const onSave = () => {
    alert(text);
  };
  return (
    <div>
      invite
      <div>
        <textarea
          onChange={(e: ChangeEvent<HTMLTextAreaElement>) => setText(e.target.value)}
        />
        <button type="button" onClick={onSave}>
          Create
        </button>
      </div>
    </div>
  );
};
