import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { AppState } from '../../redux/root-reducer';
import { InvitesState } from './reducer';
import { Invite, INVITE_ERROR_FETCHING, INVITE_FILL, INVITE_START_FETCHING, InvitesActionTypes } from './types';


export const useInvite = () => {
  const { loading, data, error } = useSelector<AppState, InvitesState>(
    (state) => state.invites,
  );
  const dispatch = useDispatch();
  const dispatchAction = (action: InvitesActionTypes) => dispatch(action);
  const add = (payload: Invite) => {
    const list = [...data, payload];
    setTimeout(() => {
      dispatchAction({
        type: INVITE_FILL,
        payload: list,
      });
    }, 1000);
  };

  const load = () => {
    dispatchAction({ type: INVITE_START_FETCHING });
    setTimeout(() => {
      if (Math.random() > 0.5) {
        dispatchAction({
          type: INVITE_FILL,
          payload: [
            {
              text: 'hi honey',
              id: 1,
            },
            {
              text: 'oh honey',
              id: 2,
            },
          ],
        });
      } else {
        dispatchAction({
          type: INVITE_ERROR_FETCHING,
          payload: 'error fetching olol',
        });
      }
    }, 1000);
  };
  return {
    loading, data, error, add, load,
  };
};
